import axios from "axios";
import { useState } from "react";
import { useEffect } from "react";
export function Chats (){
    const user = [];
    useEffect(() => {
        axios.get('http://localhost:8000/api/v1/Current/', { withCredentials: true })
            .then(response => {
                if (response.data.error) {
                    user = null; // Устанавливаем null, если есть поле error
                } else {
                    user = response.data;
                }
            })
            .catch(error => {
                console.error(error);
                setUser(null); // Устанавливаем null в случае ошибки
            });
    }, []);
    const [friends, setFriends] = useState([]);
    useEffect(() => {
        axios.get(`http://127.0.0.1:8000/api/v1/Friends/${user.id}/`)
            .then(response => {
                setFriends(response.data);
                console.log(response.data);
            })
            .catch(error => {
                console.error('Error fetching data:', error);
            });
    }, []);
    const [chat,setChat] = useState(1);
    const toggleChat = (index)=>{
        setChat(index);
    }
    return(
    <>
        {friends.map(item => {
        return(
            <div class="chat" key={item.id}>
            <img src={`http://127.0.0.1:8000${item.friend_avatar}`} alt="Friend Avatar"/>
            <span class="chat_name">{item.friend_name}</span>
        </div>);
        })}
    </>
    )
}
