import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Cookies from 'js-cookie';
import online_img from './../images/shape.png';
import send_message from './../images/mail.png';
import '../static/css/Chat.css';

export function ChatArea() {
    const [chattingWith, setChatting] = useState(null);
    const [socket, setSocket] = useState(null);
    const [receiver, setReceiver] = useState(null);
    const [messages, setMessages] = useState([]);

    useEffect(() => {
        axios.get(`http://127.0.0.1:8000/api/v1/chats/your_chat/?your_id=${Cookies.get('user_id')}`)
            .then(response => {
                setChatting(response.data);
            }).catch(error => {
                console.log(error);
            });
    }, []);

    useEffect(() => {
        if (chattingWith) {
            axios.get(`http://localhost:8000/api/v1/users/${chattingWith.chatting_with}/`, { withCredentials: true })
                .then(response => {
                    setReceiver(response.data);
                    console.log(response.data);
                })
                .catch(error => {
                    console.error(error);
                });
        }
    }, [chattingWith]);

    useEffect(() => {
        if (chattingWith) {
            setMessages([]); // Очистка состояния сообщений при новом подключении
            const ws = new WebSocket('ws://localhost:8000/ws/some/');

            ws.onopen = function () {
                const senderId = Cookies.get('user_id');
                const receiverId = chattingWith.chatting_with;
                ws.send(JSON.stringify({ sender_id: senderId, receiver_id: receiverId }));
            };

            ws.onmessage = function (event) {
                const data = JSON.parse(event.data);
                setMessages(prevMessages => [...prevMessages, data].sort((a, b) => new Date(a.time_of_message) - new Date(b.time_of_message)));
            };

            ws.onclose = function (event) {
                console.log('WebSocket connection closed:', event);
            };

            ws.onerror = function (error) {
                console.error('WebSocket error:', error);
            };

            setSocket(ws);

            return () => ws.close();
        }
    }, [chattingWith]);

    if (!receiver) {
        return null; // Или заглушка, пока данные не загрузятся
    }

    return (
        <>
            <div className="nick_name_area">
                <img src={receiver.avatar} alt="" className='friend_avatar' />
                <span>{receiver.username}</span>
                <span className="online">online</span>
                <img src={online_img} alt="" className="online_circle" />
            </div>

            <div className="chat_message_area">
                {messages.map((message, index) => (
                    <div key={index} className={message.sender === parseInt(Cookies.get('user_id')) ? 'message' : 'friend_message'}>
                        <img src={message.sender === parseInt(Cookies.get('user_id')) ? Cookies.get('user_avatar') : receiver.avatar} alt="Avatar" />
                        <p className={message.sender === parseInt(Cookies.get('user_id')) ? 'message_text' : 'friend_message_text'}>
                            {message.content}
                        </p>
                    </div>
                ))}
            </div>
            <input type="text" className='input_message' />
            <button className="send_message_bt" type="submit">
                <img src={send_message} alt="Send" />
            </button>
        </>
    );
}
