import axios from 'axios';
import upload from '../image-.png'
import { useEffect, useState } from "react";
export function Profile() {
    function getCookie(name) {
        let cookie = document.cookie.split('; ').find(row => row.startsWith(name + '='));
        return cookie ? cookie.split('=')[1] : null;
    }
    const ProfileImage = ({ imageSrc }) => {
        const [isHovered, setIsHovered] = useState(false);
    
        const handleMouseEnter = () => {
            setIsHovered(true);
        };
    
        const handleMouseLeave = () => {
            setIsHovered(false);
    }};
    const username = getCookie('name');
    const [imagePaths, setImagePaths] = useState([]);
    useEffect(() => {
        axios.get(`http://127.0.0.1:8000/api/v1/User_image/${username}/`)
            .then(response => {
                setImagePaths(response.data);
            })
            .catch(error => {
                console.error('Error fetching data:', error);
            });
    }, []);

    return (
        <div className='profile_area'>
            <img className='upload_image'style={{ opacity: isHovered ? 1 : 0 }} src={upload} alt="" />
            <img  className="profile_image" onMouseEnter={handleMouseEnter}
                onMouseLeave={handleMouseLeave} src={`http://127.0.0.1:8000${imagePaths.avatar}`} alt="" />
            <span className='nick_name'>{username}</span>
        </div>
    );
    }
