import axios from 'axios';
import upload from '../image-.png'
import { useEffect, useState } from "react";
import { getCookie } from '../getcookie';
import { useRef } from 'react';
export function Profile() {
    const username = getCookie('name');
    //Получение пользовательского аватара
    const [imagePaths, setImagePaths] = useState([]);
    const fileInputRef = useRef(null); 

    useEffect(() => {
        axios.get(`http://127.0.0.1:8000/api/v1/User_avatar/${username}/`)
            .then(response => {
                setImagePaths(response.data);
            })
            .catch(error => {
                console.error('Error fetching data:', error);
            });
    }, []);

    const handleImageClick = () => {
        fileInputRef.current.click(); // Имитируем клик на скрытый инпут
    };
    const handleFileChange = (event) => {
        const file = event.target.files[0];
        if (file) {
            const formData = new FormData();
            formData.append('avatar', file);

            axios.put(`http://127.0.0.1:8000/api/v1/User_avatar/${username}/`, formData, {
                headers: {
                    'Content-Type': 'multipart/form-data'
                }
            })
            .then(response => {
                setImagePaths(response.data); // Обновляем изображение после успешной загрузки
            })
            .catch(error => {
                console.error('Error uploading file:', error);
            });
        }
    };

    const HoverComponent = () => {
        const [isHovered, setIsHovered] = useState(false);

        const handleMouseEnter = () => {
            setIsHovered(true);
        };

        const handleMouseLeave = () => {
            setIsHovered(false);
        };


        return (
            <>
                <input
                    ref={fileInputRef}
                    type="file"
                    style={{ display: 'none' }}
                    onChange={handleFileChange}
                />
                
                <svg width="100" height="100" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg" className={isHovered ? 'upload_image_active' : 'upload_image'} onMouseEnter={handleMouseEnter}
                    onMouseLeave={handleMouseLeave}
                    onClick={handleImageClick}>
                    <path d="M12 2L6 8H10V22H14V8H18L12 2Z" fill="white"/>
                </svg>  

                <img 
                className={isHovered ? 'profile_image_active':'profile_image'}
                src={`http://127.0.0.1:8000${imagePaths.avatar}`} // я так и не понял почему оно работает только с указанием локал хоста
                onMouseEnter={handleMouseEnter}
                onMouseLeave={handleMouseLeave}
                onClick={handleImageClick}
                alt=""/>
                <span className='change_image_text'>Edit Picture</span>
            </>
        );
    };
    return (
        <div className='profile_area'>
            <HoverComponent />
            <div className="user_info">
                <div className="user_info_item">
                    
                </div>
            </div>
        </div>
    );
}
