import axios from "axios";
import { useState } from "react";
import { useEffect } from "react";
export function Chats (){
    const [userData, setUserData] = useState(null); // Начальное значение null, чтобы проверить наличие данных
    const [friends, setFriends] = useState([]);

    useEffect(() => {
        axios.get('http://localhost:8000/api/v1/Current/', { withCredentials: true })
            .then(response => {
                setUserData(response.data);
            })
            .catch(error => {
                console.error(error);
            });
    }, []);

    useEffect(() => {
        if (userData) {
            axios.get(`http://localhost:8000/api/v1/Friends/${userData.id}/`, { withCredentials: true })
                .then(response => {
                    setFriends(response.data);
                })
                .catch(error => {
                    console.error('Error fetching friends data:', error);
                });
        }
    }, [userData]);

    const [chat,setChat] = useState(friends[0]);
    const toggleChat = (index)=>{
        setChat(index);
        console.log(chat);
    }
    return(
    <>
        {friends.map(item => (
                <div
                    className={chat === item.friend_id ? 'chat_active' : 'chat'}
                    key={item.friend_id}
                    onClick={() => toggleChat(item.friend_id)}
                >
                    <img src={`http://127.0.0.1:8000${item.friend_avatar}`} alt="Friend Avatar" />
                    <span className="chat_name">{item.friend_name}</span>
                </div>
            ))}
    </>
    )
}
