import axios from "axios";
import { useState } from "react";
import { useEffect } from "react";
export function Chats (){
    const [user, setUser] = useState(null);
    const [friends, setFriends] = useState([]);

    useEffect(() => {
        axios.get('http://localhost:8000/api/v1/Current/', { withCredentials: true })
            .then(response => {
                if (response.data.error) {
                    setUser(null); // Устанавливаем null, если есть поле error
                } else {
                    setUser(response.data);
                }
            })
            .catch(error => {
                console.error(error);
                setUser(null); // Устанавливаем null в случае ошибки
            });
    }, []);

    useEffect(() => {
        if (user) { // Убедимся, что user не null перед запросом
            axios.get(`http://127.0.0.1:8000/api/v1/Friends/${user.id}/`)
                .then(response => {
                    setFriends(response.data);
                    console.log(response.data);
                })
                .catch(error => {
                    console.error('Error fetching data:', error);
                });
        }
    }, [user]);
    const [chat,setChat] = useState(null);
    const toggleChat = (index)=>{
        setChat(index);
        console.log(chat);  
    }
    return(
    <>
        {friends.map(item => {
        return(
            <div class={chat != null ? 'chat_active' : 'chat'} key={item.friend_id} onClick={()=> toggleChat(item.friend_id)}>
            <img src={`http://127.0.0.1:8000${item.friend_avatar}`} alt="Friend Avatar"/>
            <span class="chat_name">{item.friend_name}</span>
        </div>);
        })}
    </>
    )
}
