import './App.css';
import user_img from './user.png'
import chats_img from './comment.png'
import setting_img from './settings.png'
import logout_img from './logout.png'
import { Chats } from './components/Chats';
import { ChatArea } from './components/ChatArea';
import { Other } from './components/OtherMenu';
import { useEffect } from 'react';
function App() {
    {
        function checkCookie(cookieName) {
            // Получаем все куки текущего документа
            const cookies = document.cookie.split(';');

            // Проверяем наличие куки с заданным именем
            for (let i = 0; i < cookies.length; i++) {
              const cookie = cookies[i].trim();
              // Куки имеют формат "name=value"
              if (cookie.startsWith(cookieName + '=')) {
                return true; // Кука существует
              }
            }
            return false; // Кука не найдена
          }

          // Пример использования:
          const cookieExists = checkCookie('name');
          console.log('Cookie exists:', cookieExists);
    }
  return(
    <>
      <div class="main_content">
        <div class="nav">
            <div class="nav_item">
                <img src={user_img} alt=""/>
                <span>Profile</span>
            </div>
            <div class="nav_item">
                <img src={chats_img} alt=""/>
                <span>Chats</span>
            </div>
            <div class="nav_item">
                <img src={setting_img} alt=""/>
                <span>Settings</span>
            </div>
            <div class="nav_item">
                <img src={logout_img} alt=""/>
                <span>Log out</span>
            </div>
        </div>
        <div class="chat_wrapper">
            <div class="chat_list">
                <input type="text" placeholder="search"/>
                <div class="chats">
                    <Chats/>
                </div>
            </div>
            <div class="main_area">
                <ChatArea/>
            </div>
        </div>
        <div class="another_menu">

        </div>
    </div>
    </>
  );
}

export default App;
