import axios from "axios";
import { useState } from "react";
import { useEffect } from "react";
export function Chats (){
    const [userData, setUserData] = useState([]);
    useEffect(() => {
        axios.get('http://localhost:8000/api/v1/Current/', { withCredentials: true })
            .then(response => {
                setUserData(response.data);
            })
            .catch(error => {
                console.error(error);
            });
    }, []);

    console.log(userData.id)
    const [friends, setFriends] = useState([]);
    useEffect(() => {
        axios.get(`http://localhost:8000/api/v1/Friends/${userData.id}`, { withCredentials: true })
            .then(response => {
                setFriends(response.data);
            })
            .catch(error => {
                console.error('Error fetching friends data:', error);
            });
    }, []);

    const [chat,setChat] = useState(null);
    const toggleChat = (index)=>{
        setChat(index);
        console.log(chat);  
    }
    return(
    <>
        {friends.map(item => {
        return(
            <div class={chat != null ? 'chat_active' : 'chat'} key={item.friend_id} onClick={()=> toggleChat(item.friend_id)}>
            <img src={`http://127.0.0.1:8000${item.friend_avatar}`} alt="Friend Avatar"/>
            <span class="chat_name">{item.friend_name}</span>
        </div>);
        })}
    </>
    )
}
