import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Cookies from 'js-cookie';
import online_img from './../images/shape.png';
import send_message from './../images/mail.png';
import { mergeMessages } from './../mergedMessages.js';
import '../static/css/Chat.css';

export function ChatArea() {
    const [chattingWith, setChatting] = useState(null); // Обновлено для одного пользователя

    useEffect(() => {
        axios.get(`http://127.0.0.1:8000/api/v1/chats/your_chat/?your_id=${Cookies.get('user_id')}`)
            .then(response => {
                setChatting(response.data);
            }).catch(error => {
                console.log(error);
            });
    }, []);

    useEffect(() => {
        if (chattingWith) {
            const socket = new WebSocket('ws://localhost:8000/ws/some/');

            socket.onopen = function() {
                const senderId = Cookies.get('user_id');
                const receiverId = chattingWith.id;
                socket.send(JSON.stringify({ sender_id: senderId, receiver_id: receiverId }));
            };

            socket.onmessage = function(event) {
                const data = JSON.parse(event.data);
                console.log(data);
            };

            socket.onclose = function(event) {
                console.log('WebSocket connection closed:', event);
            };

            socket.onerror = function(error) {
                console.error('WebSocket error:', error);
            };

            return () => socket.close(); // Закрытие WebSocket при размонтировании компонента
        }
    }, [chattingWith]);

    return (
        <>
            <div className="nick_name_area">
                <img src='' alt="" className='friend_avatar' />
                <span></span>
                <span className="online">online</span>
                <img src={online_img} alt="" className="online_circle" />
            </div>

            <div className="chat_message_area">
                <div className='message'>
                    <img src="" alt="" />
                    <p className='message_text'>adad</p>
                </div>
                <div className="friend_message">
                    <img src="" alt="" />
                    <p className='friend_message_text'>dada</p>
                </div>
            </div>
            <button className="send_message_bt" type="submit">
                <img src={send_message} alt="Send" />
            </button>
        </>
    );
}
