import axios from 'axios';
import upload from '../image-.png'
import { useEffect, useState } from "react";
import { getCookie } from '../getcookie';
import { useRef } from 'react';
export function Profile() {
    const username = getCookie('name');
    //Получение пользовательского аватара
    const [imagePaths, setImagePaths] = useState([]);
    const fileInputRef = useRef(null); 

    useEffect(() => {
        axios.get(`http://127.0.0.1:8000/api/v1/User_image/${username}/`)
            .then(response => {
                setImagePaths(response.data);
            })
            .catch(error => {
                console.error('Error fetching data:', error);
            });
    }, []);
    const handleImageClick = () => {
        fileInputRef.current.click(); // Имитируем клик на скрытый инпут
    };

    const HoverComponent = () => {
        const [isHovered, setIsHovered] = useState(false);

        const handleMouseEnter = () => {
            setIsHovered(true);
        };

        const handleMouseLeave = () => {
            setIsHovered(false);
        };


        return (
            <>
            <input className='upload_input' type="file"/>
            <img
                className={isHovered ? 'upload_image_active' : 'upload_image'}
                src={upload}
                onMouseEnter={handleMouseEnter}
                onMouseLeave={handleMouseLeave}
                alt=""
            />
            <img 
            className={isHovered ? 'profile_image_active':'profile_image'}
            src={`http://127.0.0.1:8000${imagePaths.avatar}`} // я так и не понял почему оно работает только с указанием локал хоста
            onMouseEnter={handleMouseEnter}
            onMouseLeave={handleMouseLeave}
            alt=""/>
            <span className='nick_name'>{username}</span>
            </>
        );
    };
    return (
        <div className='profile_area'>
            <HoverComponent />
        </div>
    );
}
