import axios from 'axios';
import upload from '../image-.png'
import { useEffect, useState } from "react";
export function Profile() {
    function getCookie(name) {
        let cookie = document.cookie.split('; ').find(row => row.startsWith(name + '='));
        return cookie ? cookie.split('=')[1] : null;
    }
    document.addEventListener('DOMContentLoaded', () => {
        const profileImage = document.querySelector('.profile_image');
        const uploadImage = document.querySelector('.upload_image');
    
        profileImage.addEventListener('mouseenter', () => {
            uploadImage.style.opacity = 1; // Показываем изображение загрузки при наведении
        });
    
        profileImage.addEventListener('mouseleave', () => {
            uploadImage.style.opacity = 0; // Скрываем изображение загрузки при уходе курсора
        });
    });
    const username = getCookie('name');
    const [imagePaths, setImagePaths] = useState([]);
    useEffect(() => {
        axios.get(`http://127.0.0.1:8000/api/v1/User_image/${username}/`)
            .then(response => {
                setImagePaths(response.data);
            })
            .catch(error => {
                console.error('Error fetching data:', error);
            });
    }, []);

    return (
        <div className='profile_area'>
            <img className='upload_image' src={upload} alt="" />
            <img  className="profile_image" src={`http://127.0.0.1:8000${imagePaths.avatar}`} alt="" />
            <span className='nick_name'>{username}</span>
        </div>
    );
}
