    import chat_avatar from '../ava.jpg'
    import online_img from '../shape.png'
    import send_message from '../mail.png'
    import '../Chat.css';
    import React, { useState, useEffect, useRef } from 'react';
    import axios from 'axios';
    export function ChatArea(){


        useEffect(() => {
            if (user) { // Убедимся, что user не null перед запросом
                axios.get(`http://127.0.0.1:8000/api/v1/Friends/${user.id}/`)
                    .then(response => {
                        setFriends(response.data);
                        console.log(response.data);
                    })
                    .catch(error => {
                        console.error('Error fetching data:', error);
                    });
            }
        }, [user]);
        const [userData, setUserData] = useState([]);
        useEffect(() => {
            axios.get('http://localhost:8000/api/v1/Current/', { withCredentials: true })
                .then(response => {
                    setUserData(response.data);
                })
                .catch(error => {
                    console.error(error);
                });
        }, []);

        const [message,set_message] = useState([]);
        useEffect(() => {
            axios.get(`http://127.0.0.1:8000/api/v1/Message/`)
                .then(response => {
                    set_message(response.data);
                    response.data.map((elem)=>{
                        return(
                            console.log(elem.sender)
                        )
                    });
                })
                .catch(error => {
                    console.error('Error fetching data:', error);
                });
        }, []);


        return(
            <>
            <div class="nick_name_area">
                <img src={chat_avatar} alt="" className='friend_avatar'/>
                <span>BabyHustler</span>
                <span class="online">online</span>
                <img src={online_img} alt="" class="online_circle"/>
            </div>
                <div className="chat_message_area">
                    <div className="message">
                        <img src={`http://127.0.0.1:8000${userData.avatar}`} alt=""/>
                        <p className="message_text">
                            {
                                
                            }
                        </p>
                    </div>
                    <div className='friend_message'>
                        <p className="friend_message_text">
                        </p>
                        <img src={chat_avatar} alt=""/>
                    </div>
                </div>
                <input type="text" name="input_message" id="input_message" placeholder="Your message" className='input_message'/>
                <button className="send_message_bt" type="submit"><img src={send_message} alt=""/></button>
            </>
        );
    }
