// Часть отвечающая  за сам чат (написано очень каряво)
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Cookies from 'js-cookie';
import online_img from './../images/shape.png';
import send_message from './../images/mail.png';
import { mergeMessages } from './../mergedMessages.js';
import '../static/css/Chat.css';

export function ChatArea() {
    const [chatting_with,setChatting] = useState([]);
    useEffect(()=>{
        axios.get(`http://127.0.0.1:8000/api/v1/chats/your_chat/?your_id=${Cookies.get('user_id')}`)
        .then(response =>{
            setChatting(response.data);
        }).catch(error =>{
            console.log(error)
        });

    },[]);

        return (
        <>
                <div className="nick_name_area">
                    <img src='' alt="" className='friend_avatar'/>
                    <span></span>
                    <span className="online">online</span>
                    <img src={online_img} alt="" className="online_circle"/>
                </div>

                <div className="chat_message_area">
                    
                    <div className='message'>
                        <img src="" alt="" />
                        <p className='message_text'>adad</p>
                    </div>

                    <div className="friend_message">
                        <img src="" alt="" />
                        <p className='friend_message_text'>dada</p>
                    </div>

                </div>
                <button className="send_message_bt" type="submit"><img src='' alt="Send"/></button>
        </>
        );
}
