import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Cookies from 'js-cookie';
import chat_avatar from '../ava.jpg';
import online_img from '../shape.png';
import send_message from '../mail.png';
import '../Chat.css';

export function ChatArea() {
    const [userData, setUserData] = useState({});
    const [friendData, setFriendData] = useState({});
    const [myMessages, setMyMessages] = useState([]);
    const [friendMessages, setFriendMessages] = useState([]);

    useEffect(() => {
        // Fetch current user data
        axios.get('http://localhost:8000/api/v1/Current/', { withCredentials: true })
            .then(response => {
                setUserData(response.data);
            })
            .catch(error => {
                console.error('Error fetching current user:', error);
            });
    }, []);

    useEffect(() => {
        // Fetch messages sent by current user to friend
        if (userData.id && Cookies.get('friend_chat_id')) {
            fetchMyMessages();
        }
    }, [userData.id]);

    useEffect(() => {
        // Fetch messages sent by friend to current user
        if (Cookies.get('friend_chat_id') && userData.id) {
            fetchFriendMessages();
        }
    }, [Cookies.get('friend_chat_id'), userData.id]);

    useEffect(() => {
        // Fetch friend's data
        if (Cookies.get('friend_chat_id')) {
            fetchFriendData();
        }
    }, [Cookies.get('friend_chat_id')]);

    useEffect(() => {
        // Polling for message updates every 10 seconds (adjust as needed)
        const interval = setInterval(() => {
            if (Cookies.get('friend_chat_id')) {
                fetchMyMessages();
                fetchFriendMessages();
            }
        }, 5000); // 10 seconds interval

        return () => clearInterval(interval); // Cleanup on unmount or dependencies change
    }, []);

    const fetchMyMessages = () => {
        axios.get(`http://127.0.0.1:8000/api/v1/Messages/${userData.id}/${Cookies.get('friend_chat_id')}/`)
            .then(response => {
                setMyMessages(response.data);
            })
            .catch(error => {
                console.error('Error fetching my messages:', error);
            });
    };

    const fetchFriendMessages = () => {
        axios.get(`http://127.0.0.1:8000/api/v1/Messages/${Cookies.get('friend_chat_id')}/${userData.id}/`)
            .then(response => {
                setFriendMessages(response.data);
            })
            .catch(error => {
                console.error('Error fetching friend messages:', error);
            });
    };

    const fetchFriendData = () => {
        axios.get(`http://localhost:8000/api/v1/Users/${Cookies.get('friend_chat_id')}/`, { withCredentials: true })
            .then(response => {
                setFriendData(response.data);
            })
            .catch(error => {
                console.error('Error fetching friend data:', error);
            });
    };

    const sendMessage = () => {
        // Implement sending message functionality
    };

    return (
        <div className="chat_wrapper">
            <div className="chat_list">
                {/* Sidebar or other chat list components */}
            </div>
    
            <div className="chat_message_area">
                {myMessages.map((msg, index) => (
                    <div key={msg.id} className="message own_message">
                        <img src={`http://127.0.0.1:8000${userData.avatar}`} alt="My Avatar"/>
                        <p className="message_text">{msg.content}</p>
                    </div>
                ))}
    
                {friendMessages.map((msg, index) => (
                    <div key={msg.id} className="friend_message">
                        <p className="friend_message_text">{msg.content}</p>
                        <img src={friendData.avatar} alt="Friend's Avatar"/>
                    </div>
                ))}
            </div>
    
            <input type="text" name="input_message" id="input_message" placeholder="Your message" className='input_message'/>
            <button className="send_message_bt" type="submit" onClick={sendMessage}><img src={send_message} alt="Send"/></button>
        </div>
    );
}
