import axios from "axios";
import { useState, useEffect } from "react";

export function Chats() {
    const [userData, setUserData] = useState(null);
    const [friends, setFriends] = useState([]);
    const [chat, setChat] = useState(null); // Изначально chat установлен в null

    useEffect(() => {
        axios.get('http://localhost:8000/api/v1/Current/', { withCredentials: true })
            .then(response => {
                setUserData(response.data);
            })
            .catch(error => {
                console.error(error);
            });
    }, []);

    useEffect(() => {
        if (userData) {
            axios.get(`http://localhost:8000/api/v1/Friends/${userData.id}/`, { withCredentials: true })
                .then(response => {
                    setFriends(response.data);
                    if (response.data.length > 0) {
                        setChat(response.data[0].friend_id); // Установить начальное значение chat в friend_id первого друга
                    }
                })
                .catch(error => {
                    console.error('Ошибка при получении данных о друзьях:', error);
                });
        }
    }, [userData]);

    const toggleChat = (friendId) => {
        setChat(friendId);
        console.log(friendId);
    };

    return (
        <>
            {friends.map(item => (
                <div
                    className={chat === item.friend_id ? 'chat_active' : 'chat'}
                    key={item.friend_id}
                    onClick={() => toggleChat(item.friend_id)}
                >
                    <img src={`http://127.0.0.1:8000${item.friend_avatar}`} alt="Аватар друга" />
                    <span className="chat_name">{item.friend_name}</span>
                </div>
            ))}
        </>
    );
}
