import axios from 'axios';
import upload from '../image-.png'
import { useEffect, useState } from "react";
export function Profile() {
    function getCookie(name) {
        let cookie = document.cookie.split('; ').find(row => row.startsWith(name + '='));
        return cookie ? cookie.split('=')[1] : null;
    }
    const username = getCookie('name');
    const [imagePaths, setImagePaths] = useState([]);
    useEffect(() => {
        axios.get(`http://127.0.0.1:8000/api/v1/User_image/${username}/`)
            .then(response => {
                setImagePaths(response.data);
                console.log(response.data)
            })
            .catch(error => {
                console.error('Error fetching data:', error);
            });
    }, []);
    const HoverComponent = ({ imagePaths, username }) => {
        const [isHovered, setIsHovered] = useState(false);
        const [selectedFile, setSelectedFile] = useState(null);
        const [uploadStatus, setUploadStatus] = useState('');
    
        const handleMouseEnter = () => {
            setIsHovered(true);
        };
    
        const handleMouseLeave = () => {
            setIsHovered(false);
        };
    
        const handleFileChange = (event) => {
            setSelectedFile(event.target.files[0]);
        };
    
        const handleUpload = async () => {
            try {
                const formData = new FormData();
                formData.append('photo', selectedFile);
    
                const response = await axios.post('http://127.0.0.1:8000/upload/', formData, {
                    headers: {
                        'Content-Type': 'multipart/form-data'
                    }
                });
    
                setUploadStatus('File uploaded successfully.');
                console.log('File upload successful:', response.data);
                // Дополнительная логика по обновлению изображения или состояния UI
            } catch (error) {
                setUploadStatus('Error uploading file.');
                console.error('Error uploading file:', error);
                // Обработка ошибки, отображение сообщения об ошибке и т.д.
            }
        };
    
        return (
            <>
                <input
                    className='upload_input'
                    type="file"
                    onChange={handleFileChange}
                />
                <img
                    className={isHovered ? 'upload_image_active' : 'upload_image'}
                    src={upload}
                    onMouseEnter={handleMouseEnter}
                    onMouseLeave={handleMouseLeave}
                    alt=""
                />
                <img
                    className={isHovered ? 'profile_image_active' : 'profile_image'}
                    src={`http://127.0.0.1:8000${imagePaths.avatar}`}
                    onMouseEnter={handleMouseEnter}
                    onMouseLeave={handleMouseLeave}
                    alt=""
                />
                <span className='nick_name'>{username}</span>
                {/* Кнопка загрузки, которая появляется при наведении */}
                {isHovered && selectedFile && (
                    <button onClick={handleUpload}>Upload</button>
                )}
                {/* Отображение статуса загрузки */}
                {uploadStatus && <p>{uploadStatus}</p>}
            </>
        );
    };

    return (
        <div className='profile_area'>
            <HoverComponent />
        </div>
    );
}
